$(document).ready(function() {


    var WIDTH = 500;
    var HEIGHT = 364;

    var reset_image = function() {
        var canvas_el = document.querySelector('canvas');
        var ctx       = canvas_el.getContext('2d');

        var img = new Image();
        img.src = 'data:image/jpeg;base64,';
        img.addEventListener('load', function() {
            ctx.drawImage(img, 0, 0);
        });

    };

    var add_frame = function() {
        var canvas_el = document.querySelector('canvas');
        var ctx       = canvas_el.getContext('2d');

        var FRAME_WIDTH = 20;

        ctx.fillRect(0, 0, WIDTH, FRAME_WIDTH);
        ctx.fillRect(0, 0, FRAME_WIDTH, HEIGHT);
        ctx.fillRect(0, HEIGHT - FRAME_WIDTH, WIDTH, HEIGHT);
        ctx.fillRect(WIDTH - FRAME_WIDTH, 0, WIDTH, HEIGHT);
    };


    var grayscale = function() {
        var canvas_el = document.querySelector('canvas');
        var ctx       = canvas_el.getContext('2d');

        var image_data = ctx.getImageData(0, 0, WIDTH, HEIGHT);
        var pix = image_data.data;

        for (var i = 0, n = pix.length; i < n; i += 4) {
            var avg = (pix[i] + pix[i + 1] + pix[i + 2]) / 3;

            pix[i] = avg;       // pix[i] is the red part of the pixel
            pix[i + 1] = avg;   // pix[i+1] is the green part of the pixel
            pix[i + 2] = avg;   // pix[i+2] is the blue part of the pixel

            // i+3 is alpha (the fourth element)
        }

// Draw the ImageData at the given (x,y) coordinates.
        ctx.putImageData(image_data, 0, 0);
    };

    $('#btn-reset').click( reset_image );
    $('#btn-frame').click( add_frame );
    $('#btn-bw').click( grayscale );

    reset_image();
});